const cards=document.querySelectorAll(".expander__card");function expandCard(){cards.forEach(a=>a.classList.remove("expander__card-active")),this.classList.add("expander__card-active")}function defaultCard(){cards.forEach(a=>a.classList.remove("expander__card-active")),cards[0].classList.add("expander__card-active")}cards.forEach(a=>a.addEventListener("mouseover",expandCard)),cards.forEach(a=>a.addEventListener("mouseout",defaultCard));